			{
				$msg="<font color='red'><b>CARGO IS NOT UNSTUFFED.</b></font>";
			}
			else
			{
				if($verify_id==0)
				{
					$msg="<font color='red'><b>NOT VERIFIED YET</b></font>";
				}
			}
			
			$data['unstuff_flag']=$rtnValue;
