				$rotation_no=$this->input->post('rotation_no');
				$cond="WHERE imp_rot='$rotation_no'";
			}
			else if($search_by=="exp_rotation_no")
			{
				$rotation_no=$this->input->post('rotation_no');
				$cond="WHERE exp_rot='$rotation_no'";
			}
			
