			// $data['be_no']=$rslt_entry_dlv_dtl[0]['be_no'];
			// $data['be_dt']=$rslt_entry_dlv_dtl[0]['be_dt'];
			// $data['cp_no']=$rslt_entry_dlv_dtl[0]['cp_no'];
			// $data['cp_dt']=$rslt_entry_dlv_dtl[0]['cp_dt'];
			$data['is_entry_data']=1;
			
